let enabledAnimation=!1;const themeButtons=document.querySelectorAll(`div[id=theme-toggle]`);themeButtons.forEach(a=>{let c=`light`,d=`dark`,b=`theme`;a.addEventListener(`click`,a=>{if(!enabledAnimation){document.styleSheets[0].insertRule(`* {  --color-transition: 250ms; transition: background-color var(--color-transition), color var(--color-transition);}`);enabledAnimation=!0};let e=localStorage.getItem(b)?localStorage.getItem(b):c;let f;if(e==c){f=d}else if(e==d){f=c}else{f=c};document.documentElement.setAttribute(b,f);localStorage.setItem(b,f)})});const currentTheme=localStorage.getItem(`theme`)?localStorage.getItem(`theme`):`light`;document.documentElement.setAttribute(`theme`,currentTheme);const hamburger=document.querySelector(`#hamburger`);const mobileMenu=document.querySelector(`.menu`);hamburger.addEventListener(`click`,function(){this.children[0].classList.toggle(`active`);mobileMenu.classList.toggle(`hide`)})